import { motion } from "framer-motion";
import { Edit, Search, Trash2 } from "lucide-react";
import { useState } from "react";

const PRODUCT_DATA = [
	{ id: 1, name: "Organic Apples", category: "Groceries", price: 2.99, stock: 200, sales: 5200, image: "data:image/jpeg;base64," },
	{ id: 2, name: "Denim Jacket", category: "Fashion", price: 59.99, stock: 75, sales: 3400, image: "data:image/jpeg;base64," },
	{ id: 3, name: "Dining Table Set", category: "Furniture", price: 499.99, stock: 30, sales: 2900, image: "data:image/jpeg;base64," },
	{ id: 4, name: "Self-Help Book", category: "Books", price: 15.99, stock: 150, sales: 2400, image: "https://images.unsplash.com/photo-1512820790803-83ca734da794" },
	{ id: 5, name: "Multivitamins", category: "Health", price: 29.99, stock: 45, sales: 2000, image: "data:image/jpeg;base64," },
  	{ id: 6, name: "Novel", category: "Books", price: 12.99, stock: 100, sales: 1500, image: "data:image/jpeg;base64," },
	{ id: 7, name: "Stainless Steel Water Bottle", category: "Health", price: 19.99, stock: 75, sales: 1800, image: "data:image/jpeg;base64," },
	{ id: 8, name: "Kitchen Utensil Set", category: "Furniture", price: 39.99, stock: 90, sales: 2200, image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRNvEEWu98FjjBPqom3LssGHd9Y8vAFJtEtkQ&s" },
	{ id: 9, name: "Running Shoes", category: "Fashion", price: 89.99, stock: 60, sales: 2700, image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSQ8COy6QyYsLoA6zGFURz5HqAtc82gH5iKHQ&s" },
];

const AvailableProducts = () => {
	const [searchTerm, setSearchTerm] = useState("");
	const [filteredProducts, setFilteredProducts] = useState(PRODUCT_DATA);

	const handleSearch = (e) => {
		const term = e.target.value.toLowerCase();
		setSearchTerm(term);
		const filtered = PRODUCT_DATA.filter(
			(product) => product.name.toLowerCase().includes(term) || product.category.toLowerCase().includes(term)
		);
		setFilteredProducts(filtered);
	};

	return (
		<motion.div
			className='bg-gradient-to-r from-brown-700 to-brown-600 p-6 rounded-lg shadow-lg border border-brown-600'
			initial={{ opacity: 0, y: 20 }}
			animate={{ opacity: 1, y: 0 }}
			transition={{ delay: 0.2 }}
		>
			<div className='flex justify-between items-center mb-5'>
				<h2 className='text-2xl font-semibold text-white'>Product Overview</h2>
				<div className='relative'>
					<input
						type='text'
						placeholder='Find a product...'
						className='bg-brown-700 text-brown-200 rounded-lg pl-10 pr-4 py-2 border border-brown-500 focus:outline-none focus:ring-2 focus:ring-indigo-500'
						onChange={handleSearch}
						value={searchTerm}
					/>
					<Search className='absolute left-3 top-2.5 text-brown-400' size={18} />
				</div>
			</div>

			<div className='grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6'>
				{filteredProducts.map((product) => (
					<motion.div
						key={product.id}
						className='bg-brown-800 p-4 rounded-lg shadow-lg border border-brown-600'
						initial={{ opacity: 0 }}
						animate={{ opacity: 1 }}
						transition={{ duration: 0.3 }}
					>
						<div className='flex items-center justify-between mb-4'>
							<img
								src={product.image}
								alt={`${product.name} image`}
								className='h-24 w-24 object-cover rounded-md'
							/>
							<div className='flex flex-col items-end'>
								<h3 className='text-lg font-semibold text-white'>{product.name}</h3>
								<p className='text-sm text-brown-400'>{product.category}</p>
								<p className='text-md text-brown-400 font-bold'>${product.price.toFixed(2)}</p>
							</div>
						</div>
						<div className='flex justify-between'>
							<p className='text-sm text-brown-300'>Stock: {product.stock}</p>
							<p className='text-sm text-brown-300'>Sales: {product.sales}</p>
						</div>
						<div className='mt-4 flex justify-end'>
							<button className='text-blue-400 hover:text-blue-300 mr-2'>
								<Edit size={18} />
							</button>
							<button className='text-red-400 hover:text-red-300'>
								<Trash2 size={18} />
							</button>
						</div>
					</motion.div>
				))}
			</div>
		</motion.div>
	);
};

export default AvailableProducts;
